    private void cBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cBActionPerformed
        char tp[] = tpA.getText().toCharArray();
        String tc = "";
        
        int ncol = 5;
        int nfil = (int)((tp.length/5.0)+0.99);
        int cont = 0;
        
        char mp[][] = new char[nfil][ncol];
        System.out.println(ncol+" "+nfil);
        System.out.println(tp.length);
        
        for(int i = 0; i<nfil; i++) {
            for(int j = 0; j<ncol; j++) {              
                if(cont+1 <= tp.length) {
                    mp[i][j] = tp[cont];
                    cont++;
                }   
                else {
                    mp[i][j] = ' ';
                }
                System.out.println(i+" "+j+" "+mp[i][j]+" "+cont); 
            }
        }
        cont = 0;
        for(int i = 0; i<ncol; i++) {
            for(int j = 0; j<nfil; j++) {              
                tc = tc + mp[j][i];          
            }
        }
        tcA.setText(tc);
    }//GEN-LAST:event_cBActionPerformed

